class Solution {
public:
    int minSubArrayLen(int target, vector<int>& nums) {
        int left = 0, sum = 0, minLen = INT_MAX;

        for (int right = 0; right < nums.size(); right++) {
            sum += nums[right];

            // Shrink window while sum >= target
            while (sum >= target) {
                minLen = min(minLen, right - left + 1);
                sum -= nums[left++];
            }
        }

        return minLen == INT_MAX ? 0 : minLen;
    }
};

class Solution {
public:
    vector<int> findAnagrams(string s, string p) {
        vector<int> result;
        if (s.size() < p.size()) return result;

        vector<int> pCount(26, 0), sCount(26, 0);

        // Count chars in p and first window of s
        for (int i = 0; i < p.size(); i++) {
            pCount[p[i] - 'a']++;
            sCount[s[i] - 'a']++;
        }

        if (pCount == sCount) result.push_back(0);

        // Slide window
        for (int i = p.size(); i < s.size(); i++) {
            sCount[s[i] - 'a']++; // Add new char
            sCount[s[i - p.size()] - 'a']--; // Remove old char

            if (pCount == sCount) {
                result.push_back(i - p.size() + 1);
            }
        }

        return result;
    }
};

class Solution {
public:
    int lengthOfLongestSubstring(string s) {
        unordered_set<char> seen;
        int left = 0, maxLen = 0;

        for (int right = 0; right < s.size(); right++) {
            // Shrink window until no duplicate
            while (seen.count(s[right])) {
                seen.erase(s[left++]);
            }

            seen.insert(s[right]);
            maxLen = max(maxLen, right - left + 1);
        }

        return maxLen;
    }
};

class Codec {
public:
    // Encodes tree to string
    string serialize(TreeNode* root) {
        string result;
        serializeHelper(root, result);
        return result;
    }

    void serializeHelper(TreeNode* node, string& s) {
        if (!node) {
            s += "null,";
            return;
        }
        s += to_string(node->val) + ",";
        serializeHelper(node->left, s);
        serializeHelper(node->right, s);
    }

    // Decodes string to tree
    TreeNode* deserialize(string data) {
        queue<string> q;
        stringstream ss(data);
        string item;

        // Parse comma-separated values
        while (getline(ss, item, ',')) {
            q.push(item);
        }

        return deserializeHelper(q);
    }

    TreeNode* deserializeHelper(queue<string>& q) {
        string val = q.front();
        q.pop();

        if (val == "null") return nullptr;

        TreeNode* node = new TreeNode(stoi(val));
        node->left = deserializeHelper(q);
        node->right = deserializeHelper(q);
        return node;
    }
};

class Solution {
public:
    ListNode* reverseKGroup(ListNode* head, int k) {
        if (!head || k == 1) return head;

        // Check if we have k nodes
        ListNode* curr = head;
        for (int i = 0; i < k; i++) {
            if (!curr) return head;
            curr = curr->next;
        }

        // Reverse first k nodes
        ListNode* prev = nullptr;
        curr = head;
        for (int i = 0; i < k; i++) {
            ListNode* next = curr->next;
            curr->next = prev;
            prev = curr;
            curr = next;
        }

        // Connect with rest
        head->next = reverseKGroup(curr, k);
        return prev;
    }
};

class Solution {
public:
    int numIslands(vector<vector<char>>& grid) {
        if (grid.empty()) return 0;

        int m = grid.size(), n = grid[0].size();
        int islands = 0;

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == '1') {
                    islands++;
                    dfs(grid, i, j);
                }
            }
        }

        return islands;
    }

private:
    void dfs(vector<vector<char>>& grid, int i, int j) {
        if (i < 0 || i >= grid.size() || j < 0 || j >= grid[0].size() || grid[i][j] != '1') {
            return;
        }

        grid[i][j] = '0'; // Mark as visited
        dfs(grid, i+1, j);
        dfs(grid, i-1, j);
        dfs(grid, i, j+1);
        dfs(grid, i, j-1);
    }
};

class Solution {
public:
    int ladderLength(string beginWord, string endWord, vector<string>& wordList) {
        unordered_set<string> dict(wordList.begin(), wordList.end());
        if (!dict.count(endWord)) return 0;

        queue<string> q;
        q.push(beginWord);
        int level = 1;

        while (!q.empty()) {
            int size = q.size();
            for (int i = 0; i < size; i++) {
                string word = q.front();
                q.pop();

                if (word == endWord) return level;

                // Try all possible transformations
                for (int j = 0; j < word.length(); j++) {
                    char orig = word[j];
                    for (char c = 'a'; c <= 'z'; c++) {
                        word[j] = c;
                        if (dict.count(word)) {
                            dict.erase(word);
                            q.push(word);
                        }
                    }
                    word[j] = orig;
                }
            }
            level++;
        }

        return 0;
    }
};

class Solution {
public:
    int minDistance(string word1, string word2) {
        int m = word1.length(), n = word2.length();
        vector<vector<int>> dp(m + 1, vector<int>(n + 1));

        // Base cases
        for (int i = 0; i <= m; i++) dp[i][0] = i;
        for (int j = 0; j <= n; j++) dp[0][j] = j;

        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                if (word1[i-1] == word2[j-1]) {
                    dp[i][j] = dp[i-1][j-1];
                } else {
                    dp[i][j] = 1 + min({dp[i-1][j],    // delete
                                        dp[i][j-1],    // insert
                                        dp[i-1][j-1]}); // replace
                }
            }
        }

        return dp[m][n];
    }
};

class LRUCache {
    struct Node {
        int key, val;
        Node* prev, *next;
        Node(int k = 0, int v = 0) : key(k), val(v), prev(nullptr), next(nullptr) {}
    };

    unordered_map<int, Node*> mpp;
    Node* head, *tail;
    int cap;

public:
    LRUCache(int capacity) : cap(capacity) {
        head = new Node();
        tail = new Node();
        head->next = tail;
        tail->prev = head;
    }

    int get(int key) {
        if (!mpp.count(key)) return -1;

        Node* node = mpp[key];
        moveToHead(node);
        return node->val;
    }

    void put(int key, int value) {
        if (mpp.count(key)) {
            Node* node = mpp[key];
            node->val = value;
            moveToHead(node);
        } else {
            Node* node = new Node(key, value);
            mpp[key] = node;
            addToHead(node);

            if (mpp.size() > cap) {
                Node* last = removeTail();
                mpp.erase(last->key);
                delete last;
            }
        }
    }

private:
    void addToHead(Node* node) {
        node->prev = head;
        node->next = head->next;
        head->next->prev = node;
        head->next = node;
    }

    void removeNode(Node* node) {
        node->prev->next = node->next;
        node->next->prev = node->prev;
    }

    void moveToHead(Node* node) {
        removeNode(node);
        addToHead(node);
    }

    Node* removeTail() {
        Node* last = tail->prev;
        removeNode(last);
        return last;
    }
};

class Solution {
public:
    vector<vector<int>> merge(vector<vector<int>>& intervals) {
        if (intervals.empty()) return {};

        sort(intervals.begin(), intervals.end());
        vector<vector<int>> result;

        for (auto& interval : intervals) {
            // No overlap, add new interval
            if (result.empty() || result.back()[1] < interval[0]) {
                result.push_back(interval);
            } else {
                // Overlap, merge intervals
                result.back()[1] = max(result.back()[1], interval[1]);
            }
        }

        return result;
    }
};

class Solution {
public:
    int maxProfit(vector<int>& prices) {
        int minPrice = INT_MAX, maxProfit = 0;

        for (int price : prices) {
            // Update max profit if selling today
            maxProfit = max(maxProfit, price - minPrice);
            // Update min price seen so far
            minPrice = min(minPrice, price);
        }

        return maxProfit;
    }
};

