class Solution {
public:
    vector<vector<int>> kClosest(vector<vector<int>>& points, int k) {
        // Max heap to keep k closest points
        priority_queue<pair<int, int>> pq;

        for (int i = 0; i < points.size(); i++) {
            int dist = points[i][0] * points[i][0] + points[i][1] * points[i][1];

            if (pq.size() < k) {
                pq.push({dist, i});
            } else if (dist < pq.top().first) {
                pq.pop();
                pq.push({dist, i});
            }
        }

        vector<vector<int>> result;
        while (!pq.empty()) {
            result.push_back(points[pq.top().second]);
            pq.pop();
        }

        return result;
    }
};

class Solution {
public:
    int minMeetingRooms(vector<vector<int>>& intervals) {
        if (intervals.empty()) return 0;

        sort(intervals.begin(), intervals.end());
        priority_queue<int, vector<int>, greater<int>> pq; // min heap

        for (auto& interval : intervals) {
            // Remove meetings that have ended
            if (!pq.empty() && pq.top() <= interval[0]) {
                pq.pop();
            }
            // Add current meeting's end time
            pq.push(interval[1]);
        }

        return pq.size();
    }
};

class Solution {
public:
    ListNode* mergeKLists(vector<ListNode*>& lists) {
        auto cmp = [](ListNode* a, ListNode* b) { return a->val > b->val; };
        priority_queue<ListNode*, vector<ListNode*>, decltype(cmp)> pq(cmp);

        // Add all non-null heads to heap
        for (ListNode* head : lists) {
            if (head) pq.push(head);
        }

        ListNode dummy(0);
        ListNode* curr = &dummy;

        while (!pq.empty()) {
            ListNode* node = pq.top();
            pq.pop();

            curr->next = node;
            curr = curr->next;

            if (node->next) {
                pq.push(node->next);
            }
        }

        return dummy.next;
    }
};

class Solution {
public:
    int trap(vector<int>& height) {
        if (height.empty()) return 0;

        int left = 0, right = height.size() - 1;
        int leftMax = 0, rightMax = 0;
        int water = 0;

        while (left < right) {
            if (height[left] < height[right]) {
                if (height[left] >= leftMax) {
                    leftMax = height[left];
                } else {
                    water += leftMax - height[left];
                }
                left++;
            } else {
                if (height[right] >= rightMax) {
                    rightMax = height[right];
                } else {
                    water += rightMax - height[right];
                }
                right--;
            }
        }

        return water;
    }
};

class Solution {
public:
    vector<vector<int>> threeSum(vector<int>& nums) {
        sort(nums.begin(), nums.end());
        vector<vector<int>> result;

        for (int i = 0; i < nums.size() - 2; i++) {
            // Skip duplicates for first element
            if (i > 0 && nums[i] == nums[i-1]) continue;

            int left = i + 1, right = nums.size() - 1;

            while (left < right) {
                int sum = nums[i] + nums[left] + nums[right];

                if (sum == 0) {
                    result.push_back({nums[i], nums[left], nums[right]});

                    // Skip duplicates
                    while (left < right && nums[left] == nums[left + 1]) left++;
                    while (left < right && nums[right] == nums[right - 1]) right--;

                    left++;
                    right--;
                } else if (sum < 0) {
                    left++;
                } else {
                    right--;
                }
            }
        }

        return result;
    }
};

class Solution {
public:
    bool isValid(string s) {
        stack<char> st;

        for (char c : s) {
            if (c == '(' || c == '{' || c == '[') {
                st.push(c);
            } else {
                if (st.empty()) return false;

                char top = st.top();
                st.pop();

                if ((c == ')' && top != '(') ||
                    (c == '}' && top != '{') ||
                    (c == ']' && top != '[')) {
                    return false;
                }
            }
        }

        return st.empty();
    }
};

class Solution {
public:
    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
        ListNode dummy(0);
        ListNode* curr = &dummy;
        int carry = 0;

        while (l1 || l2 || carry) {
            int sum = carry;

            if (l1) {
                sum += l1->val;
                l1 = l1->next;
            }

            if (l2) {
                sum += l2->val;
                l2 = l2->next;
            }

            carry = sum / 10;
            curr->next = new ListNode(sum % 10);
            curr = curr->next;
        }

        return dummy.next;
    }
};

class Solution {
public:
    bool exist(vector<vector<char>>& board, string word) {
        int m = board.size(), n = board[0].size();

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (dfs(board, word, i, j, 0)) {
                    return true;
                }
            }
        }

        return false;
    }

private:
    bool dfs(vector<vector<char>>& board, string& word, int i, int j, int idx) {
        if (idx == word.length()) return true;

        if (i < 0 || i >= board.size() || j < 0 || j >= board[0].size() || 
            board[i][j] != word[idx] || board[i][j] == '#') {
            return false;
        }

        char temp = board[i][j];
        board[i][j] = '#'; // Mark as visited

        bool found = dfs(board, word, i+1, j, idx+1) ||
                     dfs(board, word, i-1, j, idx+1) ||
                     dfs(board, word, i, j+1, idx+1) ||
                     dfs(board, word, i, j-1, idx+1);

        board[i][j] = temp; // Backtrack
        return found;
    }
};

class Solution {
public:
    int maxSubArray(vector<int>& nums) {
        int maxSum = nums[0], currSum = nums[0];

        for (int i = 1; i < nums.size(); i++) {
            // Either extend current subarray or start new
            currSum = max(nums[i], currSum + nums[i]);
            maxSum = max(maxSum, currSum);
        }

        return maxSum;
    }
};

class Solution {
public:
    vector<vector<string>> suggestedProducts(vector<string>& products, string searchWord) {
        sort(products.begin(), products.end());
        vector<vector<string>> result;
        string prefix = "";

        for (char c : searchWord) {
            prefix += c;
            vector<string> suggestions;

            // Find first product with current prefix
            auto it = lower_bound(products.begin(), products.end(), prefix);

            // Collect up to 3 matching products
            for (int i = 0; i < 3 && it + i != products.end(); i++) {
                string& product = *(it + i);
                if (product.length() >= prefix.length() && 
                    product.substr(0, prefix.length()) == prefix) {
                    suggestions.push_back(product);
                } else {
                    break;
                }
            }

            result.push_back(suggestions);
        }

        return result;
    }
};

class Solution {
public:
    double findMedianSortedArrays(vector<int>& nums1, vector<int>& nums2) {
        if (nums1.size() > nums2.size()) {
            return findMedianSortedArrays(nums2, nums1);
        }

        int m = nums1.size(), n = nums2.size();
        int left = 0, right = m;

        while (left <= right) {
            int cut1 = (left + right) / 2;
            int cut2 = (m + n + 1) / 2 - cut1;

            int left1 = (cut1 == 0) ? INT_MIN : nums1[cut1 - 1];
            int left2 = (cut2 == 0) ? INT_MIN : nums2[cut2 - 1];

            int right1 = (cut1 == m) ? INT_MAX : nums1[cut1];
            int right2 = (cut2 == n) ? INT_MAX : nums2[cut2];

            if (left1 <= right2 && left2 <= right1) {
                if ((m + n) % 2 == 0) {
                    return (max(left1, left2) + min(right1, right2)) / 2.0;
                } else {
                    return max(left1, left2);
                }
            } else if (left1 > right2) {
                right = cut1 - 1;
            } else {
                left = cut1 + 1;
            }
        }

        return 1.0;
    }
};

class Solution {
public:
    bool canVisitAllRooms(vector<vector<int>>& rooms) {
        unordered_set<int> visited;
        stack<int> st;
        st.push(0);

        while (!st.empty()) {
            int room = st.top();
            st.pop();

            if (visited.count(room)) continue;
            visited.insert(room);

            // Add all keys from current room
            for (int key : rooms[room]) {
                if (!visited.count(key)) {
                    st.push(key);
                }
            }
        }

        return visited.size() == rooms.size();
    }
};

vector<int> nearestSmaller(vector<int>& a) {
    vector<int> res(a.size(), -1);
    stack<int> st;
    for (int i = 0; i < a.size(); i++) {
        while (!st.empty() && a[st.top()] >= a[i]) st.pop();
        if (!st.empty()) res[i] = a[st.top()];
        st.push(i);
    }
    return res;
}

int shortestSafePath(vector<vector<int>>& mat) {
    int m = mat.size(), n = mat[0].size();
    queue<pair<int,int>> q;
    vector<vector<int>> vis(m, vector<int>(n, 0));
    for (int i = 0; i < m; i++) {
        if (mat[i][0] == 1) {
            q.push({i, 0});
            vis[i][0] = 1;
        }
    }
    int steps = 0;
    int dirs[4][2] = {{0,1},{1,0},{-1,0},{0,-1}};
    while (!q.empty()) {
        int sz = q.size();
        while (sz--) {
            auto [x, y] = q.front(); q.pop();
            if (y == n-1) return steps;
            for (auto& d : dirs) {
                int nx = x + d[0], ny = y + d[1];
                if (nx >= 0 && nx < m && ny >= 0 && ny < n && mat[nx][ny] == 1 && !vis[nx][ny]) {
                    vis[nx][ny] = 1;
                    q.push({nx, ny});
                }
            }
        }
        steps++;
    }
    return -1;
}

int maxEqualPackages(vector<int>& a) {
    int n = a.size(), sum = accumulate(a.begin(), a.end(), 0);
    if (sum % 2) return -1;
    int target = sum / 2;
    vector<int> dp(target+1, -1);
    dp[0] = 0;
    for (int x : a) {
        for (int j = target; j >= x; j--) {
            if (dp[j-x] != -1) dp[j] = max(dp[j], dp[j-x]+1);
        }
    }
    return dp[target] == -1 ? -1 : dp[target];
}

int checksumDistinct(vector<int>& a) {
    unordered_set<int> s(a.begin(), a.end());
    int sum = 0;
    for (int x : s) sum += x;
    return sum;
}

string minWindow(string s, string t) {
    vector<int> need(128, 0);
    for (char c : t) need[c]++;
    int left = 0, cnt = 0, minLen = INT_MAX, start = 0;
    for (int right = 0; right < s.size(); right++) {
        if (--need[s[right]] >= 0) cnt++;
        while (cnt == t.size()) {
            if (right - left + 1 < minLen) minLen = right - left + 1, start = left;
            if (++need[s[left++]] > 0) cnt--;
        }
    }
    return minLen == INT_MAX ? \"\" : s.substr(start, minLen);
}

int findPeakElement(vector<int>& a) {
    int l = 0, r = a.size() - 1;
    while (l < r) {
        int m = (l + r) / 2;
        if (a[m] < a[m+1]) l = m + 1;
        else r = m;
    }
    return l;
}

int subarraySum(vector<int>& a, int k) {
    unordered_map<int, int> m;
    m[0] = 1;
    int sum = 0, res = 0;
    for (int x : a) {
        sum += x;
        res += m[sum - k];
        m[sum]++;
    }
    return res;
}

bool canFinish(int n, vector<vector<int>>& pre) {
    vector<vector<int>> g(n);
    vector<int> deg(n, 0);
    for (auto& p : pre) {
        g[p[1]].push_back(p[0]);
        deg[p[0]]++;
    }
    queue<int> q;
    for (int i = 0; i < n; i++) if (!deg[i]) q.push(i);
    int cnt = 0;
    while (!q.empty()) {
        int u = q.front(); q.pop(); cnt++;
        for (int v : g[u]) if (--deg[v] == 0) q.push(v);
    }
    return cnt == n;
}

int search(vector<int>& a, int t) {
    int l = 0, r = a.size() - 1;
    while (l <= r) {
        int m = (l + r) / 2;
        if (a[m] == t) return m;
        if (a[l] <= a[m]) {
            if (a[l] <= t && t < a[m]) r = m - 1;
            else l = m + 1;
        } else {
            if (a[m] < t && t <= a[r]) l = m + 1;
            else r = m - 1;
        }
    }
    return -1;
}

void dfs(vector<int>& a, int t, int i, vector<int>& cur, vector<vector<int>>& res) {
    if (t == 0) { res.push_back(cur); return; }
    for (int j = i; j < a.size() && a[j] <= t; j++) {
        cur.push_back(a[j]);
        dfs(a, t - a[j], j, cur, res);
        cur.pop_back();
    }
}
vector<vector<int>> combinationSum(vector<int>& a, int t) {
    sort(a.begin(), a.end());
    vector<vector<int>> res;
    vector<int> cur;
    dfs(a, t, 0, cur, res);
    return res;
}

vector<vector<int>> insert(vector<vector<int>>& a, vector<int>& ni) {
    vector<vector<int>> res;
    int i = 0, n = a.size();
    while (i < n && a[i][1] < ni[0]) res.push_back(a[i++]);
    while (i < n && a[i][0] <= ni[1]) {
        ni[0] = min(ni[0], a[i][0]);
        ni[1] = max(ni[1], a[i][1]);
        i++;
    }
    res.push_back(ni);
    while (i < n) res.push_back(a[i++]);
    return res;
}

int findMin(vector<int>& a) {
    int l = 0, r = a.size() - 1;
    while (l < r) {
        int m = (l + r) / 2;
        if (a[m] > a[r]) l = m + 1;
        else r = m;
    }
    return a[l];
}

Node* cloneGraph(Node* node) {
    if (!node) return nullptr;
    unordered_map<Node*, Node*> m;
    function<Node*(Node*)> dfs = [&](Node* n) {
        if (m.count(n)) return m[n];
        Node* copy = new Node(n->val);
        m[n] = copy;
        for (Node* nei : n->neighbors) copy->neighbors.push_back(dfs(nei));
        return copy;
    };
    return dfs(node);
}

vector<int> productExceptSelf(vector<int>& a) {
    int n = a.size();
    vector<int> res(n, 1);
    int left = 1, right = 1;
    for (int i = 0; i < n; i++) {
        res[i] *= left;
        left *= a[i];
    }
    for (int i = n-1; i >= 0; i--) {
        res[i] *= right;
        right *= a[i];
    }
    return res;
}

vector<int> spiralOrder(vector<vector<int>>& mat) {
    vector<int> res;
    int m = mat.size(), n = mat[0].size();
    int top = 0, bot = m-1, left = 0, right = n-1;
    while (top <= bot && left <= right) {
        for (int j = left; j <= right; j++) res.push_back(mat[top][j]);
        top++;
        for (int i = top; i <= bot; i++) res.push_back(mat[i][right]);
        right--;
        if (top <= bot) for (int j = right; j >= left; j--) res.push_back(mat[bot][j]);
        bot--;
        if (left <= right) for (int i = bot; i >= top; i--) res.push_back(mat[i][left]);
        left++;
    }
    return res;
}

void setZeroes(vector<vector<int>>& mat) {
    int m = mat.size(), n = mat[0].size();
    bool row0 = false, col0 = false;
    for (int i = 0; i < m; i++) if (mat[i][0] == 0) col0 = true;
    for (int j = 0; j < n; j++) if (mat[0][j] == 0) row0 = true;
    for (int i = 1; i < m; i++) for (int j = 1; j < n; j++) if (mat[i][j] == 0) mat[i][0] = mat[0][j] = 0;
    for (int i = 1; i < m; i++) for (int j = 1; j < n; j++) if (mat[i][0] == 0 || mat[0][j] == 0) mat[i][j] = 0;
    if (row0) for (int j = 0; j < n; j++) mat[0][j] = 0;
    if (col0) for (int i = 0; i < m; i++) mat[i][0] = 0;
}
